import React, { useState, useEffect } from 'react';

const AddEditForm = ({ onSave, dataToEdit, closeModal }) => {
  const [formData, setFormData] = useState({
    nama_barang: '',
    stok: '',
    jumlah_terjual: '',
    tanggal_transaksi: '',
    jenis_barang: '' // akan menggunakan dropdown
  });

  useEffect(() => {
    if (dataToEdit) {
      setFormData(dataToEdit);
    }
  }, [dataToEdit]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
    closeModal();
    setFormData({
      nama_barang: '',
      stok: '',
      jumlah_terjual: '',
      tanggal_transaksi: '',
      jenis_barang: ''
    });
  };

  return (
    // Modal Wrapper
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 transition-opacity duration-300 ease-in-out">
      <div className="bg-white dark:bg-gray-100 text-black dark:text-white p-8 rounded-lg shadow-xl w-96 animate__animated animate__fadeIn">
        <h2 className="text-2xl font-semibold mb-4">
          {dataToEdit ? 'Edit' : 'Add'} Data Penjualan
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="flex flex-col space-y-2">
            <label className="text-sm font-medium">Nama Barang</label>
            <input
              type="text"
              name="nama_barang"
              value={formData.nama_barang}
              onChange={handleChange}
              placeholder="Nama Barang"
              className="border border-gray-300 p-3 rounded-md focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div className="flex flex-col space-y-2">
            <label className="text-sm font-medium">Stok</label>
            <input
              type="number"
              name="stok"
              value={formData.stok}
              onChange={handleChange}
              placeholder="Stok"
              className="border border-gray-300 dark:border-gray-400 p-3 rounded-md focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div className="flex flex-col space-y-2">
            <label className="text-sm font-medium">Jumlah Terjual</label>
            <input
              type="number"
              name="jumlah_terjual"
              value={formData.jumlah_terjual}
              onChange={handleChange}
              placeholder="Jumlah Terjual"
              className="border border-gray-300 dark:border-gray-400 p-3 rounded-md focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div className="flex flex-col space-y-2">
            <label className="text-sm font-medium">Tanggal Transaksi</label>
            <input
              type="date"
              name="tanggal_transaksi"
              value={formData.tanggal_transaksi}
              onChange={handleChange}
              className="border border-gray-300 dark:border-gray-400 p-3 rounded-md focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          {/* Dropdown untuk Jenis Barang */}
          <div className="flex flex-col space-y-2">
            <label className="text-sm font-medium">Jenis Barang</label>
            <select
              name="jenis_barang"
              value={formData.jenis_barang}
              onChange={handleChange}
              className="border border-gray-300 dark:border-gray-400 p-3 rounded-md focus:ring-2 focus:ring-blue-500"
              required
            >
              <option value="" disabled>Pilih Jenis Barang</option>
              <option value="Konsumsi">Konsumsi</option>
              <option value="Pembersih">Pembersih</option>
            </select>
          </div>

          {/* Button Group */}
          <div className="flex justify-between items-center mt-6 space-x-4">
            <button
              type="submit"
              className="bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-colors duration-300"
            >
              {dataToEdit ? 'Update' : 'Add'} Data
            </button>
            <button
              type="button"
              onClick={closeModal}
              className="bg-gray-500 text-white py-2 px-4 rounded-lg hover:bg-gray-600 transition-colors duration-300"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddEditForm;
