import React, { useState, useEffect } from 'react';

const AddEditForm = ({ onSave, dataToEdit, closeModal }) => {
  const [formData, setFormData] = useState({
    produk_id: '',
    transaksi_id: '',
    stok: '',
    jumlah_terjual: '',
  });

  // Mengisi data form jika sedang dalam mode edit
  useEffect(() => {
    if (dataToEdit) {
      setFormData({
        produk_id: dataToEdit.produk_id || '',
        transaksi_id: dataToEdit.transaksi_id || '',
        stok: dataToEdit.stok || '',
        jumlah_terjual: dataToEdit.jumlah_terjual || '',
      });
    }
  }, [dataToEdit]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validasi input
    if (!formData.produk_id || !formData.transaksi_id || !formData.stok || !formData.jumlah_terjual) {
      alert('Semua field harus diisi!');
      return;
    }

    // Kirim data ke parent component
    onSave(formData);
    closeModal(); // Tutup modal setelah data disimpan

    // Reset form setelah submit
    setFormData({
      produk_id: '',
      transaksi_id: '',
      stok: '',
      jumlah_terjual: '',
    });
  };

  return (
    <form
      className="bg-white shadow-md rounded-lg p-6 max-w-md mx-auto"
      onSubmit={handleSubmit}
    >
      <h2 className="text-2xl font-semibold mb-4 text-center">
        {dataToEdit ? 'Edit Data Penjualan' : 'Tambah Data Penjualan'}
      </h2>

      {/* Produk ID */}
      <div className="mb-4">
        <label htmlFor="produk_id" className="block text-gray-700 font-medium mb-2">
          Produk ID
        </label>
        <input
          type="text"
          id="produk_id"
          name="produk_id"
          className="w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring focus:ring-blue-300"
          placeholder="Masukkan ID Produk"
          value={formData.produk_id}
          onChange={handleChange}
        />
      </div>

      {/* Transaksi ID */}
      <div className="mb-4">
        <label htmlFor="transaksi_id" className="block text-gray-700 font-medium mb-2">
          Transaksi ID
        </label>
        <input
          type="text"
          id="transaksi_id"
          name="transaksi_id"
          className="w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring focus:ring-blue-300"
          placeholder="Masukkan ID Transaksi"
          value={formData.transaksi_id}
          onChange={handleChange}
        />
      </div>

      {/* Stok */}
      <div className="mb-4">
        <label htmlFor="stok" className="block text-gray-700 font-medium mb-2">
          Stok
        </label>
        <input
          type="number"
          id="stok"
          name="stok"
          className="w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring focus:ring-blue-300"
          placeholder="Masukkan jumlah stok"
          value={formData.stok}
          onChange={handleChange}
        />
      </div>

      {/* Jumlah Terjual */}
      <div className="mb-4">
        <label htmlFor="jumlah_terjual" className="block text-gray-700 font-medium mb-2">
          Jumlah Terjual
        </label>
        <input
          type="number"
          id="jumlah_terjual"
          name="jumlah_terjual"
          className="w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring focus:ring-blue-300"
          placeholder="Masukkan jumlah terjual"
          value={formData.jumlah_terjual}
          onChange={handleChange}
        />
      </div>

      {/* Tombol */}
      <div className="flex justify-between items-center mt-6">
        <button
          type="button"
          className="px-4 py-2 bg-gray-300 text-gray-800 font-medium rounded-md shadow-sm hover:bg-gray-400"
          onClick={closeModal}
        >
          Batal
        </button>
        <button
          type="submit"
          className="px-4 py-2 bg-blue-500 text-white font-medium rounded-md shadow-sm hover:bg-blue-600"
        >
          {dataToEdit ? 'Simpan Perubahan' : 'Tambah Data'}
        </button>
      </div>
    </form>
  );
};

export default AddEditForm;
