import React from 'react';

const SearchAndSort = ({ searchTerm, handleSearch, handleSort }) => {
  return (
    <section className="mb-8">
      <h2 className="text-2xl font-semibold mb-4">Search and Sort</h2>
      <div className="flex flex-wrap gap-4 mb-6 items-center">
        <div className="flex-1 min-w-[250px]">
          <input
            type="text"
            placeholder="Search by Nama Barang"
            value={searchTerm}
            onChange={handleSearch}
            className="py-2 px-4 border rounded-lg w-full"
          />
        </div>

        <div className="flex gap-4">
          <button
            onClick={() => handleSort('nama_barang')}
            className="bg-blue-600 text-white py-2 px-6 rounded-lg hover:bg-blue-700"
          >
            Sort by Nama Barang
          </button>
          <button
            onClick={() => handleSort('tanggal_transaksi')}
            className="bg-blue-600 text-white py-2 px-6 rounded-lg hover:bg-blue-700"
          >
            Sort by Tanggal Transaksi
          </button>
        </div>
      </div>
    </section>
  );
};

export default SearchAndSort;
