import React, { useState } from 'react';

const AddData = () => {
  const [formData, setFormData] = useState({
    namaBarang: '',
    stok: '',
    jumlahTerjual: '',
    tanggalTransaksi: '',
    jenisBarang: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Kirim data ke backend (via API)
    console.log(formData);
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Tambah Data Penjualan</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="form-control">
          <label className="label">
            <span className="label-text">Nama Barang</span>
          </label>
          <input 
            type="text" 
            name="namaBarang" 
            value={formData.namaBarang} 
            onChange={handleChange} 
            className="input input-bordered w-full" 
            placeholder="Nama Barang" 
            required 
          />
        </div>

        <div className="form-control">
          <label className="label">
            <span className="label-text">Stok</span>
          </label>
          <input 
            type="number" 
            name="stok" 
            value={formData.stok} 
            onChange={handleChange} 
            className="input input-bordered w-full" 
            placeholder="Stok" 
            required 
          />
        </div>

        <div className="form-control">
          <label className="label">
            <span className="label-text">Jumlah Terjual</span>
          </label>
          <input 
            type="number" 
            name="jumlahTerjual" 
            value={formData.jumlahTerjual} 
            onChange={handleChange} 
            className="input input-bordered w-full" 
            placeholder="Jumlah Terjual" 
            required 
          />
        </div>

        <div className="form-control">
          <label className="label">
            <span className="label-text">Tanggal Transaksi</span>
          </label>
          <input 
            type="date" 
            name="tanggalTransaksi" 
            value={formData.tanggalTransaksi} 
            onChange={handleChange} 
            className="input input-bordered w-full" 
            required 
          />
        </div>

        <div className="form-control">
          <label className="label">
            <span className="label-text">Jenis Barang</span>
          </label>
          <select 
            name="jenisBarang" 
            value={formData.jenisBarang} 
            onChange={handleChange} 
            className="select select-bordered w-full" 
            required
          >
            <option value="">Pilih Jenis Barang</option>
            <option value="Konsumsi">Konsumsi</option>
            <option value="Pembersih">Pembersih</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary w-full mt-4">
          Simpan Data
        </button>
      </form>
    </div>
  );
};

export default AddData;
