import React from 'react';

const Header = ({ searchTerm, handleSearch, filterStartDate, setFilterStartDate, filterEndDate, setFilterEndDate, handleDateFilter, handleComparison }) => {
  return (
    <header className="flex flex-col sm:flex-row justify-between items-center mb-6">
      {/* Header Title */}
      <h1 className="text-4xl font-semibold text-gray-800 mb-4 sm:mb-0">CRUD Penjualan</h1>

      {/* Search Input */}
      <div className="mb-6 sm:mb-0 w-full sm:w-1/3">
        <input
          type="text"
          placeholder="Search by Nama Barang"
          value={searchTerm}
          onChange={handleSearch}
          className="py-2 px-4 border rounded-lg w-full"
        />
      </div>

      {/* Date Range Filter */}
      <div className="flex space-x-4 mb-6 sm:mb-0">
        <input
          type="date"
          value={filterStartDate}
          onChange={(e) => setFilterStartDate(e.target.value)}
          className="py-2 px-4 border rounded-lg w-full sm:w-1/3"
        />
        <input
          type="date"
          value={filterEndDate}
          onChange={(e) => setFilterEndDate(e.target.value)}
          className="py-2 px-4 border rounded-lg w-full sm:w-1/3"
        />
        <button
          onClick={handleDateFilter}
          className="bg-green-600 text-white py-2 px-6 rounded-lg hover:bg-green-700 sm:w-1/3"
        >
          Filter
        </button>
      </div>

      {/* Action Buttons */}
      <div className="flex space-x-4 mb-6 sm:mb-0">
        <button
          onClick={handleComparison}
          className="bg-yellow-600 text-white py-2 px-6 rounded-lg hover:bg-yellow-700"
        >
          Compare Transactions by Product Type
        </button>
      </div>
    </header>
  );
};

export default Header;
