import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Table from '../components/Table';
import AddEditForm from '../components/AddEditForm';
import 'boxicons/css/boxicons.min.css';

const Home = () => {
  const [penjualan, setPenjualan] = useState([]);
  const [editing, setEditing] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [search, setSearch] = useState('');
  const [sortBy, setSortBy] = useState('nama_barang');
  const [sortOrder, setSortOrder] = useState('ASC');

  // Mengambil data penjualan dari API dengan fitur pencarian dan pengurutan
  useEffect(() => {
    axios.get('http://localhost:5000/api/penjualan', {
      params: { search, sortBy, sortOrder }
    })
      .then(response => {
        setPenjualan(response.data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, [search, sortBy, sortOrder]);

  // Menambahkan atau memperbarui data penjualan
  const handleSave = (data) => {
    if (editing) {
      axios.put(`http://localhost:5000/api/penjualan/${editing.id}`, data)
        .then(() => {
          setPenjualan(prev => prev.map(item => (item.id === editing.id ? data : item)));
          setEditing(null);
        })
        .catch(error => console.error('Error updating data:', error));
    } else {
      axios.post('http://localhost:5000/api/penjualan', data)
        .then(response => {
          setPenjualan([...penjualan, { ...data, id: response.data.id }]);
        })
        .catch(error => console.error('Error adding data:', error));
    }
  };

  // Menghapus data penjualan
  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/api/penjualan/${id}`)
      .then(() => {
        setPenjualan(prev => prev.filter(item => item.id !== id));
      })
      .catch(error => console.error('Error deleting data:', error));
  };

  // Menyunting data penjualan
  const handleEdit = (data) => {
    setEditing(data);
    setIsModalOpen(true);
  };

  // Menutup modal
  const closeModal = () => {
    setIsModalOpen(false);
    setEditing(null);
  };

  return (
    <div className="min-h-screen text-gray-900 font-ubuntu">
      <div className="container mx-auto p-6">
        {/* Header */}
        <header className="flex justify-between items-center mb-6">
          <h1 className="text-4xl font-semibold">CRUD Penjualan</h1>
        </header>

        {/* Tombol untuk menambah data baru */}
        <div className="text-right mb-4">
          <button
            onClick={() => {
              setEditing(null);
              setIsModalOpen(true);
            }}
            className="bg-blue-600 text-white py-2 px-6 rounded-lg hover:bg-blue-700 transition-colors duration-300"
          >
            Add New Data
          </button>
        </div>

        {/* Pencarian dan Sorting dalam satu baris */}
        <div className="mb-4 flex gap-4 items-center">
          {/* Pencarian */}
          <div className="flex-1">
            <label htmlFor="search" className="block text-sm font-semibold mb-2">Search</label>
            <input
              id="search"
              type="text"
              placeholder="Nama Barang or Tanggal"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="bg-white py-2 px-4 border-black rounded-lg w-full"
            />
          </div>

          {/* Sorting */}
          <div className="flex gap-4">
            <div>
              <label htmlFor="sortBy" className="block text-sm font-semibold mb-2">Sort by</label>
              <select
                id="sortBy"
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="py-2 px-4 border rounded-lg"
              >
                <option value="nama_barang">Nama Barang</option>
                <option value="tanggal_transaksi">Tanggal Transaksi</option>
              </select>
            </div>
            <div>
              <label htmlFor="sortOrder" className="block text-sm font-semibold mb-2">Order</label>
              <select
                id="sortOrder"
                value={sortOrder}
                onChange={(e) => setSortOrder(e.target.value)}
                className="py-2 px-4 border rounded-lg"
              >
                <option value="ASC">Ascending</option>
                <option value="DESC">Descending</option>
              </select>
            </div>
          </div>
        </div>

        {/* Tabel untuk menampilkan data penjualan */}
        <Table data={penjualan} onDelete={handleDelete} onEdit={handleEdit} />

        {/* Modal untuk tambah/edit data */}
        {isModalOpen && (
          <AddEditForm
            onSave={handleSave}
            dataToEdit={editing}
            closeModal={closeModal}
          />
        )}
      </div>
    </div>
  );
};

export default Home;
