import React from 'react';

const FilterAndComparison = ({ filterStartDate, setFilterStartDate, filterEndDate, setFilterEndDate, handleDateFilter, handleComparison, comparisonData }) => {
  return (
    <section className="mb-8">
      <h2 className="text-2xl font-semibold mb-4">Filter and Comparison</h2>
      <div className="flex space-x-4 mb-6">
        <input
          type="date"
          value={filterStartDate}
          onChange={(e) => setFilterStartDate(e.target.value)}
          className="py-2 px-4 border rounded-lg w-full sm:w-1/3"
        />
        <input
          type="date"
          value={filterEndDate}
          onChange={(e) => setFilterEndDate(e.target.value)}
          className="py-2 px-4 border rounded-lg w-full sm:w-1/3"
        />
        <button
          onClick={handleDateFilter}
          className="bg-green-600 text-white py-2 px-6 rounded-lg hover:bg-green-700"
        >
          Filter
        </button>
        <button
          onClick={handleComparison}
          className="bg-yellow-600 text-white py-2 px-10 rounded-lg hover:bg-yellow-700"
        >
          Compare Transactions by Product Type
        </button>
      </div>

      {comparisonData && (
        <div className="bg-gray-100 p-4 rounded-lg">
          <h3 className="font-semibold text-gray-800">Comparison Result</h3>
          <p>Highest Sales: {comparisonData.highest.name} - {comparisonData.highest.totalSold}</p>
          <p>Lowest Sales: {comparisonData.lowest.name} - {comparisonData.lowest.totalSold}</p>
        </div>
      )}
    </section>
  );
};

export default FilterAndComparison;
