import React, { useState } from 'react';

const Table = ({ data, onDelete, onEdit, onChangeJenis }) => {
  // State untuk menyimpan nilai yang dipilih pada dropdown
  const [selectedJenis, setSelectedJenis] = useState('');

  // Handler ketika memilih jenis barang di dropdown
  const handleJenisChange = (e, id) => {
    const value = e.target.value;
    setSelectedJenis(value);  // Update state untuk dropdown yang dipilih
    onChangeJenis(id, value); // Kirimkan id dan value yang dipilih ke parent
  };

  return (
    <table className="table-auto w-full border-collapse">
      <thead>
        <tr>
          <th className="border p-2">Nama Barang</th>
          <th className="border p-2">Stok</th>
          <th className="border p-2">Jumlah Terjual</th>
          <th className="border p-2">Tanggal Transaksi</th>
          <th className="border p-2">Jenis Barang</th>
          <th className="border p-2">Actions</th>
        </tr>
      </thead>
      <tbody>
        {data.map((item) => (
          <tr key={item.id}>
            <td className="border p-2">{item.nama_barang}</td>
            <td className="border p-2">{item.stok}</td>
            <td className="border p-2">{item.jumlah_terjual}</td>
            <td className="border p-2">{item.tanggal_transaksi}</td>
            <td className="border p-2">
              {/* Dropdown untuk memilih Jenis Barang */}
              <select 
                value={item.jenis_barang} 
                onChange={(e) => handleJenisChange(e, item.id)} 
                className="select select-bordered w-full"
              >
                <option value="Konsumsi">Konsumsi</option>
                <option value="Pembersih">Pembersih</option>
              </select>
            </td>
            <td className="border p-2">
              <button onClick={() => onEdit(item)} className="bg-blue-500 text-white p-2 mr-2">Edit</button>
              <button onClick={() => onDelete(item.id)} className="bg-red-500 text-white p-2">Delete</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Table;
