// src/components/Header.jsx
import React from 'react';

const Header = ({ searchTerm, setSearchTerm, filterStartDate, setFilterStartDate, filterEndDate, setFilterEndDate, handleDateFilter, handleComparison }) => {
  return (
    <header className="flex flex-col sm:flex-row justify-between items-center mb-6">
      <div className="flex justify-between items-center w-full sm:w-auto">
        <h1 className="text-4xl font-semibold text-gray-800 mb-4 sm:mb-0">CRUD Penjualan</h1>
      </div>

      {/* Search Input */}
      <div className="mb-6 sm:mb-0 sm:ml-6">
        <input
          type="text"
          placeholder="Search by Nama Barang"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="py-2 px-4 border rounded-lg w-full sm:w-64"
        />
      </div>

      {/* Date Range Filter */}
      <div className="flex space-x-4 mb-6 sm:mb-0 sm:ml-6">
        <input
          type="date"
          value={filterStartDate}
          onChange={(e) => setFilterStartDate(e.target.value)}
          className="py-2 px-4 border rounded-lg w-full sm:w-32"
        />
        <input
          type="date"
          value={filterEndDate}
          onChange={(e) => setFilterEndDate(e.target.value)}
          className="py-2 px-4 border rounded-lg w-full sm:w-32"
        />
        <button
          onClick={handleDateFilter}
          className="bg-green-600 text-white py-2 px-6 rounded-lg hover:bg-green-700"
        >
          Filter
        </button>
      </div>

      {/* Action Buttons */}
      <div className="flex justify-between w-full sm:w-auto mt-4 sm:mt-0">
        {/* Add New Data Button */}
        <button
          onClick={() => {}}
          className="bg-blue-600 text-white py-2 px-6 rounded-lg hover:bg-blue-700 transition-colors duration-300"
        >
          Add New Data
        </button>

        {/* Compare Button */}
        <button
          onClick={handleComparison}
          className="bg-yellow-600 text-white py-2 px-6 rounded-lg hover:bg-yellow-700"
        >
          Compare Transactions by Product Type
        </button>
      </div>
    </header>
  );
};

export default Header;
