import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Table from '../components/Table';
import AddEditForm from '../components/AddEditForm';
import 'boxicons/css/boxicons.min.css';

const Home = () => {
  const [penjualan, setPenjualan] = useState([]);
  const [editing, setEditing] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortField, setSortField] = useState('nama_barang'); // Default: nama_barang
  const [sortOrder, setSortOrder] = useState('asc'); // Default: ascending
  const [filterStartDate, setFilterStartDate] = useState('');
  const [filterEndDate, setFilterEndDate] = useState('');
  const [comparisonData, setComparisonData] = useState(null);

  // Mengambil data penjualan dari API
  useEffect(() => {
    axios.get('http://localhost:5000/api/penjualan')
      .then(response => {
        setPenjualan(response.data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // Menambahkan atau memperbarui data penjualan
  const handleSave = (data) => {
    if (editing) {
      axios.put(`http://localhost:5000/api/penjualan/${editing.id}`, data)
        .then(() => {
          setPenjualan(prev => prev.map(item => (item.id === editing.id ? data : item)));
          setEditing(null);
        })
        .catch(error => console.error('Error updating data:', error));
    } else {
      axios.post('http://localhost:5000/api/penjualan', data)
        .then(response => {
          setPenjualan([...penjualan, { ...data, id: response.data.id }]);
        })
        .catch(error => console.error('Error adding data:', error));
    }
  };

  // Menghapus data penjualan
  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/api/penjualan/${id}`)
      .then(() => {
        setPenjualan(prev => prev.filter(item => item.id !== id));
      })
      .catch(error => console.error('Error deleting data:', error));
  };

  // Menyunting data penjualan
  const handleEdit = (data) => {
    setEditing(data);
    setIsModalOpen(true); // Buka modal saat editing
  };

  // Menutup modal
  const closeModal = () => {
    setIsModalOpen(false);
    setEditing(null);
  };

  // --- 1. Pencarian dan Pengurutan ---
  // Fungsi untuk menangani pencarian
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  // Fungsi untuk mengurutkan data
  const handleSort = (field) => {
    const newSortOrder = sortField === field && sortOrder === 'asc' ? 'desc' : 'asc';
    setSortField(field);
    setSortOrder(newSortOrder);
  };

  // Filter dan urutkan data berdasarkan pencarian dan sorting
  const filteredData = penjualan.filter(item =>
    item.nama_barang.toLowerCase().includes(searchTerm.toLowerCase())
  ).sort((a, b) => {
    if (sortField === 'nama_barang') {
      return sortOrder === 'asc'
        ? a.nama_barang.localeCompare(b.nama_barang)
        : b.nama_barang.localeCompare(a.nama_barang);
    } else if (sortField === 'tanggal_transaksi') {
      return sortOrder === 'asc'
        ? new Date(a.tanggal_transaksi) - new Date(b.tanggal_transaksi)
        : new Date(b.tanggal_transaksi) - new Date(a.tanggal_transaksi);
    }
    return 0;
  });

  // --- 2. Perbandingan Jenis Barang ---
  // Fungsi untuk membandingkan jenis barang
  const handleComparison = () => {
    axios.get(`http://localhost:5000/api/penjualan/comparison`, {
      params: {
        startDate: filterStartDate,
        endDate: filterEndDate
      }
    })
      .then(response => setComparisonData(response.data))
      .catch(error => console.error('Error comparing data:', error));
  };

  // --- 3. Filter Rentang Waktu ---
  // Fungsi untuk filter data berdasarkan rentang waktu
  const handleDateFilter = () => {
    if (filterStartDate && filterEndDate) {
      axios.get(`http://localhost:5000/api/penjualan/filter`, {
        params: {
          startDate: filterStartDate,
          endDate: filterEndDate,
        }
      })
        .then(response => setPenjualan(response.data))
        .catch(error => console.error('Error filtering data:', error));
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 font-ubuntu">
      <div className="container mx-auto p-6">
        {/* Header */}
        <header className="flex justify-between items-center mb-6">
          <h1 className="text-4xl font-semibold text-gray-800">CRUD Penjualan</h1>
        </header>

        <br />

        {/* --- 1. Pencarian dan Filter --- */}
        <div className="flex flex-wrap gap-4 mb-6 items-center">
          {/* Input Pencarian */}
          <div className="flex-1 min-w-[250px]">
            <input
              type="text"
              placeholder="Search by Nama Barang"
              value={searchTerm}
              onChange={handleSearch}
              className="py-2 px-4 border rounded-lg w-full"
            />
          </div>

          {/* Filter Rentang Tanggal */}
          <div className="flex space-x-4">
            <input
              type="date"
              value={filterStartDate}
              onChange={(e) => setFilterStartDate(e.target.value)}
              className="py-2 px-4 border rounded-lg w-full sm:w-1/3"
            />
            <input
              type="date"
              value={filterEndDate}
              onChange={(e) => setFilterEndDate(e.target.value)}
              className="py-2 px-4 border rounded-lg w-full sm:w-1/3"
            />
            <button
              onClick={handleDateFilter}
              className="bg-green-600 text-white py-2 px-12 rounded-lg hover:bg-green-700"
            >
              Filter
            </button>
          </div>
        </div>

        {/* --- 2. Tombol untuk Menambah Data dan Perbandingan --- */}
        <div className="flex justify-between items-center mb-6">
          {/* Tombol Add New Data */}
          <button
            onClick={() => {
              setEditing(null);
              setIsModalOpen(true); // Buka modal untuk tambah data baru
            }}
            className="bg-blue-600 text-white py-2 px-6 rounded-lg hover:bg-blue-700 transition-colors duration-300"
          >
            Add New Data
          </button>

          {/* Tombol Compare Transactions by Product Type */}
          <button
            onClick={handleComparison}
            className="bg-yellow-600 text-white py-2 px-6 rounded-lg hover:bg-yellow-700"
          >
            Compare Transactions by Product Type
          </button>
        </div>

        {/* --- 2. Display Hasil Perbandingan --- */}
        {comparisonData && (
          <div className="bg-gray-100 p-4 rounded-lg mb-6">
            <h3 className="font-semibold text-gray-800">Comparison Result</h3>
            <p>Highest Sales: {comparisonData.highest.name} - {comparisonData.highest.totalSold}</p>
            <p>Lowest Sales: {comparisonData.lowest.name} - {comparisonData.lowest.totalSold}</p>
          </div>
        )}

        {/* --- 3. Tabel untuk Menampilkan Data Penjualan --- */}
        <Table
          data={filteredData}
          onDelete={handleDelete}
          onEdit={handleEdit}
          onSort={handleSort}
          sortField={sortField}
          sortOrder={sortOrder}
        />

        {/* Modal untuk tambah/edit data */}
        {isModalOpen && (
          <AddEditForm
            onSave={handleSave}
            dataToEdit={editing}
            closeModal={closeModal}
          />
        )}
      </div>
    </div>
  );
};

export default Home;
