import React, { useState, useEffect } from 'react';

const AddEditForm = ({ onSave, dataToEdit, closeModal }) => {
  const [formData, setFormData] = useState({
    nama_barang: '',
    stok: '',
    jumlah_terjual: '',
    tanggal_transaksi: '',
    jenis_barang: ''
  });

  // Jika ada data yang diedit, set data ke formData
  useEffect(() => {
    if (dataToEdit) {
      setFormData(dataToEdit);
    }
  }, [dataToEdit]);

  // Fungsi untuk menangani perubahan input
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  // Fungsi untuk menangani submit form
  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
    closeModal(); // Tutup modal setelah menyimpan
    setFormData({
      nama_barang: '',
      stok: '',
      jumlah_terjual: '',
      tanggal_transaksi: '',
      jenis_barang: ''
    });
  };

  return (
    // Modal Wrapper
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg w-96">
        <h2 className="text-2xl font-semibold mb-4">
          {dataToEdit ? 'Edit' : 'Add'} Data Penjualan
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            name="nama_barang"
            value={formData.nama_barang}
            onChange={handleChange}
            placeholder="Nama Barang"
            className="border p-2 w-full"
            required
          />
          <input
            type="number"
            name="stok"
            value={formData.stok}
            onChange={handleChange}
            placeholder="Stok"
            className="border p-2 w-full"
            required
          />
          <input
            type="number"
            name="jumlah_terjual"
            value={formData.jumlah_terjual}
            onChange={handleChange}
            placeholder="Jumlah Terjual"
            className="border p-2 w-full"
            required
          />
          <input
            type="date"
            name="tanggal_transaksi"
            value={formData.tanggal_transaksi}
            onChange={handleChange}
            className="border p-2 w-full"
            required
          />
          <input
            type="text"
            name="jenis_barang"
            value={formData.jenis_barang}
            onChange={handleChange}
            placeholder="Jenis Barang"
            className="border p-2 w-full"
            required
          />
          <div className="flex justify-between items-center mt-4">
            <button
              type="submit"
              className="bg-green-500 text-white p-2 rounded"
            >
              {dataToEdit ? 'Update' : 'Add'} Data
            </button>
            <button
              type="button"
              onClick={closeModal}
              className="bg-gray-500 text-white p-2 rounded"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddEditForm;
