import React, { useState, useEffect } from 'react';

const AddEditForm = ({ onSave, dataToEdit, closeModal }) => {
  const [formData, setFormData] = useState({
    nama_barang: '',
    stok: '',
    jumlah_terjual: '',
    tanggal_transaksi: '',
    jenis_barang: '',
  });

  useEffect(() => {
    if (dataToEdit) {
      const formattedDate = dataToEdit.tanggal_transaksi
        ? new Date(dataToEdit.tanggal_transaksi).toISOString().split('T')[0]
        : '';
      setFormData({
        ...dataToEdit,
        tanggal_transaksi: formattedDate,
      });
    }
  }, [dataToEdit]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedData = {
      ...formData,
      tanggal_transaksi: formData.tanggal_transaksi
        ? new Date(formData.tanggal_transaksi).toISOString()
        : '',
      updated_at: new Date().toISOString(),
    };
    onSave(formattedData);
    closeModal();
    setFormData({
      nama_barang: '',
      stok: '',
      jumlah_terjual: '',
      tanggal_transaksi: '',
      jenis_barang: '',
    });
  };

  return (
    <form className="flex-col py-10 px-10 md:py-20 md:px-20 items-center justify-center shadow-lg rounded-xl bg-white" onSubmit={handleSubmit}>
      <h1 className="font-semibold text-md md:text-lg text-center py-3 text-gray-600">
        {dataToEdit ? 'Edit Data Penjualan' : 'Tambahkan Data'}
      </h1>
      <div className="flex flex-wrap py-3 items-center justify-center gap-y-3 gap-x-5 md:gap-y-5 md:gap-x-5">
        {/* Nama Barang */}
        <div className="flex flex-col md:flex-row gap-2">
          <label htmlFor="nama_barang" className="font-medium text-gray-600">
            Nama Barang:
          </label>
          <input
            type="text"
            id="nama_barang"
            name="nama_barang"
            className="rounded-md w-full md:w-[250px] shadow-sm px-2 border border-gray-400"
            placeholder="Nama barang"
            value={formData.nama_barang || ''}
            onChange={handleChange}
          />
        </div>
        {/* Tanggal */}
        <div className="flex flex-col md:flex-row gap-2">
          <label htmlFor="tanggal_transaksi" className="font-medium text-gray-600">
            Tanggal Transaksi:
          </label>
          <input
            type="date"
            id="tanggal_transaksi"
            name="tanggal_transaksi"
            className="rounded-md w-full md:w-[250px] shadow-sm px-2 border border-gray-400"
            value={formData.tanggal_transaksi || ''}
            onChange={handleChange}
          />
        </div>
        {/* Jenis Barang */}
        <div className="flex flex-col md:flex-row gap-2">
          <label htmlFor="jenis_barang" className="font-medium text-gray-600">
            Jenis Barang:
          </label>
          <select
            id="jenis_barang"
            name="jenis_barang"
            className="rounded-md w-full md:w-[250px] shadow-sm px-2 border border-gray-400"
            value={formData.jenis_barang || ''}
            onChange={handleChange}
          >
            <option value="">Pilih Jenis Barang</option>
            <option value="Elektronik">Elektronik</option>
            <option value="Peralatan Rumah">Peralatan Rumah</option>
            <option value="Pakaian">Pakaian</option>
            {/* Tambahkan jenis barang lainnya */}
          </select>
        </div>
        {/* Stok */}
        <div className="flex flex-col md:flex-row gap-2">
          <label htmlFor="stok" className="font-medium text-gray-600">
            Stok:
          </label>
          <input
            type="number"
            id="stok"
            name="stok"
            className="rounded-md w-full md:w-[250px] shadow-sm px-2 border border-gray-400"
            placeholder="Masukkan Stok Barang"
            value={formData.stok || ''}
            onChange={handleChange}
          />
        </div>
        {/* Jumlah Terjual */}
        <div className="flex flex-col md:flex-row gap-2">
          <label htmlFor="jumlah_terjual" className="font-medium text-gray-600">
            Jumlah Terjual:
          </label>
          <input
            type="number"
            id="jumlah_terjual"
            name="jumlah_terjual"
            className="rounded-md w-full md:w-[250px] shadow-sm px-2 border border-gray-400"
            placeholder="Jumlah Barang yang Terjual"
            value={formData.jumlah_terjual || ''}
            onChange={handleChange}
          />
        </div>
      </div>
      {/* Tombol Submit */}
      <div className="flex items-center justify-center py-3">
        <button
          type="submit"
          className="px-3 py-2 rounded-md bg-blue-500 hover:bg-blue-700 text-white text-xs md:text-sm font-medium shadow-lg"
        >
          {dataToEdit ? 'Update' : 'Submit'}
        </button>
      </div>
    </form>
  );
};

export default AddEditForm;
